(6, 1, '2025-11-24 07:38:52', '2025-11-24 07:38:52', '<!-- wp:group {\"className\":\"home-section1\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section1\"><!-- wp:image {\"id\":18,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/logo.svg\" alt=\"\" class=\"wp-image-18\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {\"level\":1,\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"300\"}}} -->\n<h1 class=\"wp-block-heading\" style=\"font-style:normal;font-weight:300\"><strong>where the earth breathes,</strong> <em>and time slows down.</em></h1>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section2\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section2\"><!-- wp:image {\"id\":53,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/bg-hero-dros.webp\" alt=\"\" class=\"wp-image-53\"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:image {\"id\":56,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/bg-hero-dros-reveal.webp\" alt=\"\" class=\"wp-image-56\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section3\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section3\"><!-- wp:paragraph {\"fontSize\":\"body-big\"} -->\n<p class=\"has-body-big-font-size\">Between the sea and the hills of Pantai Loji lies a home built from the soil itself  a sanctuary of silence, slowness, and simplicity.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":62,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/leaf.webp\" alt=\"\" class=\"wp-image-62\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section4\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section4\"><!-- wp:image {\"id\":65,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/bg-intro-dros.webp\" alt=\"\" class=\"wp-image-65\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section5\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section5\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":68,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/intro-dros-1.webp\" alt=\"\" class=\"wp-image-68\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph {\"style\":{\"typography\":{\"fontStyle\":\"normal\",\"fontWeight\":\"300\"}}} -->\n<p style=\"font-style:normal;font-weight:300\">concept of omah tanah<br> by <strong>Budi Pradono</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:image {\"id\":69,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/intro-dros-2.webp\" alt=\"\" class=\"wp-image-69\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section6\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section6\"><!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\">built with earth, guided by soul.</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>Inspired by the philosophy of Omah Tanah, DRos House is crafted from soil and nature  an architectural poem by <strong>Douglas &amp; Rossa</strong>, where every wall tells a story of warmth, humility, and harmony.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph {\"className\":\"link\"} -->\n<p class=\"link\"><strong><a href=\"http://localhost:8080/dros/about/\">read full story</a></strong></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->\n\n<!-- wp:image {\"id\":71,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/omah-tanah.webp\" alt=\"\" class=\"wp-image-71\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section7\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section7\"><!-- wp:image {\"id\":77,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/bg-omah-tanah-dros.webp\" alt=\"\" class=\"wp-image-77\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section8\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section8\"><!-- wp:image {\"id\":80,\"sizeSlug\":\"full\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-full\"><img src=\"http://localhost:8080/dros/wp-content/uploads/2025/11/homesection8bg.webp\" alt=\"\" class=\"wp-image-80\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:group -->\n\n<!-- wp:group {\"className\":\"home-section9\",\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group home-section9\"><!-- wp:heading -->\n<h2 class=\"wp-block-heading\"><strong>An Ode to the Earth.</strong></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>DRos House stands as a dialogue between architecture and landscape  blending raw textures, natural light, and local craftsmanship.</p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:group -->', 'Home', '', 'publish', 'closed', 'closed', '', 'home', '', '', '2025-11-25 13:06:22', '2025-11-25 13:06:22', '', 0, 'http://localhost:8080/dros/?page_id=6', 0, 'page', '', 0),
